; ============================================================
; journey.asm - Unknown Runes RPG
; All English strings XOR-encrypted with key 0xCFA044
; Decrypt: LOAD 3 bytes, XOR with 0x1FF1AD (LE), STORE back
; Generated by genJourney.py
; ============================================================

; -------- MEMORY LAYOUT (STOREI/LOADI addresses) --------
; 0x100000 = player HP            0x100003 = enemy HP
; 0x100006 = temp var             0x100009 = temp var 2
; 0x10000C = fn_print: str addr   0x10000F = fn_print: str len
; 0x100012 = fn_print: loop idx
; 0x100015 = fn_check: ans addr   0x100018 = fn_check: ans len
; 0x10001B = fn_check: loop idx   0x10001E = fn_check: XOR key
; 0x100080 = input buffer (64B)   0x1000C0 = answer buffer (64B)
; XOR key (LE word): 0x1FF1AD

; ===================== CODE START =====================
    JMP main

; ========================================================
; fn_print: decrypt-in-place (3-byte XOR), print, re-encrypt
; IN:  RA = string addr, RB = padded length (mult of 3)
; ========================================================
fn_print:
    STOREI RA, 0x10000C
    STOREI RB, 0x10000F
    MZERO RC
    STOREI RC, 0x100012
fn_pr_dec:
    LOADI RA, 0x100012
    LOADI RB, 0x10000F
    JGE RA, RB, fn_pr_print
    LOADI RB, 0x10000C
    ADD RB, RB, RA
    LOAD RC, RB
    MOV RA, 0x1FF1AD
    XOR RC, RC, RA
    STORE RB, RC
    LOADI RA, 0x100012
    ADDI RA, 3
    STOREI RA, 0x100012
    JMP fn_pr_dec
fn_pr_print:
    MOV RA, 2
    LOADI RB, 0x10000C
    MZERO RC
    SYSCALL RA, RB, RC
    MZERO RA
    STOREI RA, 0x100012
fn_pr_enc:
    LOADI RA, 0x100012
    LOADI RB, 0x10000F
    JGE RA, RB, fn_pr_ret
    LOADI RB, 0x10000C
    ADD RB, RB, RA
    LOAD RC, RB
    MOV RA, 0x1FF1AD
    XOR RC, RC, RA
    STORE RB, RC
    LOADI RA, 0x100012
    ADDI RA, 3
    STOREI RA, 0x100012
    JMP fn_pr_enc
fn_pr_ret:
    RET

fn_read_str:
    MOV RA, 4
    MOV RB, 0x100080
    MOV RC, 60
    SYSCALL RA, RB, RC
    RET

; fn_check_answer: decrypt expected answer, compare with input
; IN: RA = encrypted answer addr, RB = padded len
; OUT: RA = 0 if match
fn_check_answer:
    STOREI RA, 0x100015
    STOREI RB, 0x100018
    MZERO RC
    STOREI RC, 0x10001B
fn_ca_loop:
    LOADI RA, 0x10001B
    LOADI RB, 0x100018
    JGE RA, RB, fn_ca_cmp
    LOADI RB, 0x100015
    ADD RB, RB, RA
    LOAD RC, RB
    LOADI RA, 0x10001E
    XOR RC, RC, RA
    LOADI RA, 0x10001B
    MOV RB, 0x1000C0
    ADD RA, RB, RA
    STORE RA, RC
    LOADI RA, 0x10001B
    ADDI RA, 3
    STOREI RA, 0x10001B
    JMP fn_ca_loop
fn_ca_cmp:
    MOV RA, 6
    MOV RB, 0x100080
    MOV RC, 0x1000C0
    SYSCALL RA, RB, RC
    RET

